pragma solidity ^0.4.10;

// structs look like this:
/*
struct Funder {
  address addr;
  uint amount;
}

// The Ethereum Virtual Machine has three areas where it can store data,
// storage, memory and the stack.
// Storage is persistent between function calls and transactions. Storage is a
// key-value store that maps 256-bit words to 256-bit words. It is not possible
// to enumerate storage and it is costly to read and modify.
// Memory is more costly the larger it grows (it scales quadratically).
// The EVM is not a register machine but a stack machine, so all computations
// are performed on an data area called the stack. It has a maximum size of 1024
// elements and contains words of 256 bits.

Data location
Every complex type, i.e. arrays and structs, has an additional annotation, the
“data location”, about whether it is stored in memory or in storage. Depending
on the context, there is always a default, but it can be overridden by appending
either storage or memory to the type. The default for function parameters
(including return parameters) is memory, the default for local variables is
storage and the location is forced to storage for state variables (obviously)

Assignments to local storage variables only assign a reference though, and this
reference always points to the state variable even if the latter is changed in
the meantime. On the other hand, assignments from a memory stored reference type
to another memory-stored reference type do not create a copy.

Arrays can have a compile-time fixed size or they can be dynamic.
Creating arrays with variable length in memory can be done using the new keyword.
*/

import 'contracts/rewardCoin/SafeMath.sol';
import 'contracts/rewardCoin/ERC20Interface.sol';

contract RewardToken is ERC20Interface {
  using SafeMath for uint;
  //TODO figure out if these should be private instead of internal
  struct RewardRoleEntity {
      bool isRewardRole;
  }

  bytes32 internal _name;
  uint8 internal _decimals;
  uint256 internal _totalSupply;

  // ultimately, we would like to express an authorization framework, such that
  // different classes of users are defined and functions in the api are allowed/disallowed
  // based upon a user's set of privledges.

  mapping (address => uint256) internal balances;
  mapping (address => mapping (address => uint256)) internal allowed;
  mapping (address => bool) rewardRoleAddresses;
  // mapping (address => bool) otherRoleAddresses;
  address public _testAddr;

  address public owner = msg.sender;
  uint public creationTime = now;

  // An array of 5 dynamic arrays of uint is uint[][5] (note that the notation
  // is reversed when compared to some other languages)
  constructor(bytes32 name, uint8 decimals, uint256 totalSupply)
  public {
    _name = name;
    _decimals = decimals;
    _totalSupply = totalSupply;
    balances[msg.sender] = totalSupply;
  }

  modifier onlyBy(address account) {
    require(
      msg.sender == account,
      "Sender not authorized."
    );
    _;
    // Do not forget the "_;"! It will
    // be replaced by the actual function
    // body when the modifier is used.
  }
  modifier onlyByClassA() {
    require(
      rewardRoleAddresses[msg.sender] == true,
      "Sender not authorized."
    );
    _;
  }
  function changeOwner(address newOwner)
  public onlyBy(owner) {
    owner = newOwner;
  }
  function addAuthorizedEntityClassA(address addr)
  public onlyBy(owner) returns(bool){
    rewardRoleAddresses[addr] = true;
    return true;
  }
  function removeAuthorizedEntityClassA(address addr)
  public returns(bool) {
    delete rewardRoleAddresses[addr];
  }
  function isClassAAuthorized(address addr)
  public view returns(bool){
    return rewardRoleAddresses[addr];
  }
  function reward(address _to, uint256 _value)
  public onlyByClassA returns(bool){
    require(_to != address(0));
    require(_value <= balances[owner]);
    balances[owner] = SafeMath.sub(balances[owner], _value);
    balances[_to] = SafeMath.add(balances[_to], _value);
    return true;
  }

  // *************************************************************************
  // ****************************** BEGIN ERC20 ******************************
  // *************************************************************************
  // totalSupply - Get the total token supply
  // balanceOf - Get the token balance of an address
  // transfer - Send tokens to another address
  // approve - Give another address permission to spend a certain allowance of tokens
  // allowance - Check the allowance that one address has for another
  // transferFrom - Spend the allowance that one address has for another */

  function totalSupply()
  public constant returns (uint) {
    return _totalSupply;
  }

  // https://github.com/ethereum/EIPs/issues/223
  // If the _to address is a contract, these tokens will get transfered to the
  // contract and will be irretrievable. Consider implementing ERC223 to
  // accomodate this issue.
  //
  // A contract which is aware of RewardCoin/ERC20 should interact with the
  // contract through the approve/transferFrom methods.
  //
  // ERC223 is a cool idea, but it's only useful for ERC223-to-ERC223 transfers.
  // We should definitely safeguards to transfer() so that transfer to contracts
  // is not allowed. Even though some contract may be able to handle the tokens,
  // the approve/transferFrom technique makes that more certain.
  // Another option would be to simple claim any token sent to a contract via
  // transfer(), but this could operational overhead when angry users want to
  // reclaim their tokens. On the upside, free tokens in the case where the user
  // doesn't complain.
  function transfer(address _to, uint256 _value)
  public returns(bool) {
    // Address(0) is 0x0. It is the burn address for ETH.
    // When used in the tx.to field, will cause the tx to create a new contract
    require(_to != address(0));
    require(_value <= balances[msg.sender]);
    balances[msg.sender] = SafeMath.sub(balances[msg.sender], _value);
    balances[_to] = SafeMath.add(balances[_to], _value);
    emit Transfer(msg.sender, _to, _value);
    return true;
  }

  function balanceOf(address _owner)
  public view returns (uint256 balance) {
    return balances[_owner];
  }

  function transferFrom(address _from, address _to, uint256 _value)
  public returns (bool) {
    require(_to != address(0));
    require(_value <= balances[_from]);
    require(_value <= allowed[_from][msg.sender]);
    balances[_from] = SafeMath.sub(balances[_from], _value);
    balances[_to] = SafeMath.add(balances[_to], _value);
    allowed[_from][msg.sender] = SafeMath.sub(allowed[_from][msg.sender], _value);
    emit Transfer(_from, _to, _value);
    return true;
  }

  // Approve allows a token holder to allocate some coins to an address without
  // specifying the amount. This has a few usecases, but the main reason this
  // is done is for contract-to-contract interoperability. Since contracts
  // cannot listen for events so they will never be notified when they receive
  // tokens.
  //
  // If a user wants to send RewardCoin tokens to another contract, the user
  // should first approve the transfer to the contract's address. The user
  // can then call a function in the contract(e.g. buyWithRewardCoin) that will
  // will then call transferFrom.
  function approve(address _spender, uint256 _value)
  public returns (bool) {
    allowed[msg.sender][_spender] = _value;
    emit Approval(msg.sender, _spender, _value);
    return true;
  }

  function allowance(address _owner, address _spender)
  public view returns (uint256) {
    return allowed[_owner][_spender];
  }

  // Allows incremental changes to allowed[]
  function increaseApproval(address _spender, uint _addedValue)
  public returns (bool) {
    allowed[msg.sender][_spender] = SafeMath.add(allowed[msg.sender][_spender], _addedValue);
    emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
    return true;
  }

  function decreaseApproval(address _spender, uint _subtractedValue)
  public returns (bool) {
    uint oldValue = allowed[msg.sender][_spender];
    if (_subtractedValue > oldValue) {
      allowed[msg.sender][_spender] = 0;
    } else {
      allowed[msg.sender][_spender] = SafeMath.sub(oldValue, _subtractedValue);
    }
    emit Approval(msg.sender, _spender, allowed[msg.sender][_spender]);
    return true;
  }
  // *************************************************************************
  // ****************************** END ERC20 ******************************
  // *************************************************************************
}
